mapper.select:
select * from orders


        and user_id = #{userId}


        and status = #{status}


order by order_time desc

select * from orders


        and number = #{number}


        and phone = #{phone}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

mapper.update:
update orders set status = 7, cancel_time = now(),
cancel_reason = "付款时间超时,订单已取消!"
where status = #{status} and order_time < #{time}
